
CREATE TABLE `respondent_registration` ( `id` BIGINT NOT NULL AUTO_INCREMENT , `respondent_id` BIGINT NOT NULL , `first_name` VARCHAR(256) NULL , `last_name` VARCHAR(256) NULL , `email` VARCHAR(512) NULL , `phone_id` BIGINT NULL , PRIMARY KEY (`id`)) engine = InnoDB;
CREATE TABLE `respondent_business_registration` ( `id` BIGINT NOT NULL AUTO_INCREMENT , `respondent_id` BIGINT NOT NULL , `business_id` BIGINT NOT NULL , PRIMARY KEY (`id`)) engine = InnoDB;
